import {useRef, useState} from "react";
import ResultsTable from "./ResultsTable";
import {LineBreak} from "../LineBreak";

function QueryDBForm() {
    const queryInputRef = useRef();
    const [hasResults, setHasResults] = useState(false);
    const queryResults = [
        {
            "user_id": 1,
            "username": "user1",
            "email": "user1@email.com"
        },
        {
            "user_id": 2,
            "username": "user2",
            "email": "user2@email.com"
        },
        {
            "user_id": 3,
            "username": "user3",
            "email": "user3@email.com"
        }
    ];

    function submit(event) {
        event.preventDefault();

        const submittedData = {
            query: queryInputRef.current.value,
        }

        fetch('http://127.0.0.1:8000/api/v1/database-connections/query-remote-db/',
            {
                method: "POST",
                body: JSON.stringify(submittedData),
                headers: {
                    'Content-Type': "application/json"
                }
            }
        ).then((response) => {
            return response.json();
        }).then((data) => {
            return (
                <div>
                    <form onSubmit={submit}>
                        <div>
                            <label htmlFor="query-panel">Query Panel: </label>
                            <textarea rows="10" cols="50" id="query-panel" ref={queryInputRef}
                                      required/>
                        </div>
                        <br/>
                        <div>
                            <button className='btn'>Submit</button>
                        </div>
                    </form>
                    <LineBreak/>
                    <ResultsTable results={data}/> : null
                </div>
            )
        });
    }

    return (
        <div>
            <form onSubmit={submit}>
                <div>
                    <label htmlFor="query-panel">Query Panel: </label>
                    <textarea rows="10" cols="50" id="query-panel" ref={queryInputRef} required/>
                </div>
                <br/>
                <div>
                    <button className='btn'>Submit</button>
                </div>
            </form>
            <LineBreak/>
            <ResultsTable results={queryResults}/> : null
        </div>
    );
}

export default QueryDBForm;
