import {ConfirmationModal} from "../ConfirmationModal";
import {Backdrop} from "../Backdrop";


function UserDetail(props) {

    function makeDeleteFunk(id) {
        function _delete() {
            console.log(id)
            props.onDelete(id);
        }

        return _delete;
    }

    const onDeleteFunk = makeDeleteFunk(props.id);

    const confirmationModal = <ConfirmationModal onCancel={props.onCancel} onDelete={onDeleteFunk}/>

    return (
        <div>
            <li key={props.id}>
                {props.email + " - " + props.name}
                <button className='btn' value='delete-user' onClick={props.onClick}>Delete</button>
                {props.isModalVisible ? confirmationModal : null}
                {props.isModalVisible ? <Backdrop/> : null}
            </li>
            <br/>
        </div>
    )
}

export default UserDetail;
