import {useState, useEffect} from 'react'

import {LineBreak} from "../components/LineBreak";
import UserDetail from "../components/user_detail/UserDetail";
import {ConfirmationModal} from "../components/user_detail/ConfirmationModal";
import {Backdrop} from "../components/user_detail/Backdrop";


function ManageUsers() {

    const [isModalVisible, setModelVisible] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const [loadedUserData, setLoadedUserData] = useState([])
    const [isBadRequest, setIsBadRequest] = useState(false)

    useEffect(() => {
        setIsLoading(true)
        fetch(
            "http://127.0.0.1:8000/api/v1/users/"
        ).then((response) => {
            return response.json();
        }).then((data) => {
            setIsLoading(false);
            setLoadedUserData(data);
        }).catch(error => {
                setIsBadRequest(true);
                setIsLoading(false);
            }
        );
    }, [])


    if (isLoading) {
        return (
            <div>
                <LineBreak/>
                <p>Loading....</p>
            </div>
        );
    }

    function confirmDelete() {
        setModelVisible(true)
    }

    function cancelDelete() {
        setModelVisible(false)
    }

    function deleteUser(userId) {
        console.log(userId)
        setModelVisible(false);
    }

    if (isBadRequest && !isLoading) {
        return (
            <div className='card'>
                <h2>Manage Users</h2>
                <LineBreak/>
                Bad request
            </div>
        );
    } else {
        return (
            <div className='card'>
                <h2>Manage Users</h2>
                <LineBreak/>
                <ul>
                    {
                        loadedUserData.map((user) => {
                            return (
                                <div>
                                    <li className='list'>
                                        {user.email + " - " + user.name}
                                        <button className='btn' value='delete-user'
                                                onClick={confirmDelete}>Delete
                                        </button>
                                        <ConfirmationModal onCancel={cancelDelete}
                                                           onDelete={deleteUser}
                                                           isModalVisible={isModalVisible}
                                                           id={user.id}
                                        />
                                        <Backdrop isModalVisible={isModalVisible}/>
                                    </li>
                                    <br/>
                                </div>
                            )
                        })
                    }
                </ul>
            </div>
        );
    }
}

export default ManageUsers;
