import {LineBreak} from "../components/LineBreak";
import {useRef} from "react";


function ConnectToDB() {
    const hostInputRef = useRef()
    const portInputRef = useRef()
    const databaseInputRef = useRef()
    const userInputRef = useRef()
    const passwordInputRef = useRef()

    function submit(event) {
        event.preventDefault();
        const submittedHost = hostInputRef.current.value;
        const submittedPort = portInputRef.current.value;
        const submittedDatabase = databaseInputRef.current.value;
        const submittedUser = userInputRef.current.value;
        const submittedPassword = passwordInputRef.current.value;

        const submittedData = {
            host: submittedHost,
            port: submittedPort,
            database: submittedDatabase,
            user: submittedUser,
            password: submittedPassword
        };

        const postParameters = {
            request: "POST",
            body: JSON.stringify(submittedData)
        }

        fetch('http://127.0.0.1:8000/api/v1/database-connections/set-database-config/', postParameters)
    }

    return (
        <div className='card'>
            <h2>Connect To Database</h2>
            <LineBreak/>
            <form className onSubmit={submit}>
                <div>
                    <label htmlFor="host">Host: </label>
                    <input type="text" id="host" ref={hostInputRef} required/>
                </div>
                <div>
                    <label htmlFor="port">Port: </label>
                    <input type="text" id="port" ref={portInputRef} required/>
                </div>
                <div>
                    <label htmlFor="database">Database: </label>
                    <input type="text" id="database" ref={databaseInputRef} required/>
                </div>
                <div>
                    <label htmlFor="user">User: </label>
                    <input type="text" id="user" ref={userInputRef} required/>
                </div>
                <div>
                    <label htmlFor="password">Password: </label>
                    <input type="password" id="password" ref={passwordInputRef} required/>
                </div>
                <div>
                    <button>Connect</button>
                </div>
            </form>
        </div>
    );
}

export default ConnectToDB;
