import {Component} from "react";
import Row from "./Row";


class ResultsTable extends Component {
    constructor(props) {
        super(props);
        this.state = {
            badQueryMessage: ""
        }
    }


    getHeader = function () {
        if (this.props.data.includes("bad query")) {
            this.setState({badQueryMessage: this.props.data})
            return <div/>
        }
        let columns = Object.keys(this.props.data[0]);
        return columns.map((column) => {
            return (
                <th>
                    {column.toUpperCase()}
                </th>
            );
        })
    }

    getRows = function () {
        if (this.props.data.includes("bad query")) {
            return <div/>
        }
        let rows = this.props.data;
        let indexes = Object.keys(this.props.data[0]);
        return rows.map((row) => {
            return (
                <tr>
                    <Row data={row} keys={indexes}/>
                </tr>
            );
        })
    }

    render() {
        return (
            <div>
                {this.state.badQueryMessage ? <h3>{this.state.badQueryMessage}</h3> : null}
                <table>
                    <tr>
                        {this.getHeader()}
                    </tr>
                    <tbody>
                    {this.getRows()}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default ResultsTable;
