import {useEffect, useState} from "react";

function ConnectionStatus() {

    useEffect(() => {
        fetch(
            "http://127.0.0.1:8000/api/v1/database-connections/get-status/"
        ).then((response) => {
            return response.json();
        }).then((data) => {
            return (
                <div>
                    {data.connection_status}
                </div>
            );
        });

    }, [])
}

export default ConnectionStatus;
