import {useState} from 'react'

import {LineBreak} from "../components/LineBreak";
import {ConfirmationModal} from "../components/ConfirmationModal";
import {Backdrop} from "../components/Backdrop";


const MOCK_USER_DATA = [
    {id: "1", email: "email1@email.com", name: "user1"},
    {id: "2", email: "email2@email.com", name: "user2"},
    {id: "3", email: "email3@email.com", name: "user3"}
]

function ManageUsers() {

    const [isModalVisible, setModelVisible] = useState(false);

    function deleteUser(userId) {
        console.log("delete user: " + userId)
        setModelVisible(false)
    }

    function confirmDelete() {
        setModelVisible(true)
    }

    function cancelDelete() {
        setModelVisible(false)
    }

    function getUserJSX(id, email, name) {
        return (
            <div>
                id
                email
                name
                <button className='btn' value='delete-user' onClick={confirmDelete}>Delete</button>
                {isModalVisible ?
                    <ConfirmationModal onCancel={cancelDelete} onDelete={deleteUser(id)}/> : null}
                {isModalVisible ? <Backdrop/> : null}
            </div>
        )
    }

    return (
        <div className='card'>
            <h2>Manage Users</h2>
            <LineBreak/>
            {
                MOCK_USER_DATA.map((user) => {
                    return getUserJSX(user.id, user.name, user.email);
                })}
        </div>
    );
}

export default ManageUsers;
