import {useRef} from "react";

function QueryDataBaseForm() {
    const queryInputRef = useRef()

    function submit(event) {
        event.preventDefault();

        const submittedData = {
            host: queryInputRef.current.value,
        }

        fetch('http://127.0.0.1:8000/api/v1/database-connections/set-database-config/',
            {
                method: "POST",
                body: JSON.stringify(submittedData),
                headers: {
                    'Content-Type': "application/json"
                }
            }
        );
    }

    return (
        <form onSubmit={submit}>
            <div>
                <label htmlFor="host">Host: </label>
                <textarea rows="10" cols="50" ref={queryInputRef} required/>
            </div>
        </form>
    );
}

export default QueryDataBaseForm;
