import {Component} from "react";
import Row from "./Row";


class ResultsTable extends Component {
    constructor(props) {
        super(props);
        this.getHeaders = this.getHeaders.bind(this);
        this.getRows = this.getRows.bind(this);
        this.state = {
            badQueryMessage: ""
        }
    }


    getHeaders() {
        let columns = Object.keys(this.props.data[0]);
        return columns.map((column) => {
            return (
                <th>
                    {column.toUpperCase()}
                </th>
            );
        })
    }

    getRows() {
        let rows = this.props.data;
        if (!rows.includes("bad query")) {
            let indexes = Object.keys(this.props.data[0]);
            return rows.map((row) => {
                return (
                    <tr>
                        <Row data={row} keys={indexes}/>
                    </tr>
                );
            })
        }
    }

    render() {
        if (this.props.data.includes("bad query")) {
            this.setState({badQueryMessage: this.props.data})
        }
        debugger;
        if (!this.state.badQueryMessage) {
            return (
                <div>
                    <table>
                        <tr>
                            {this.getHeaders()}
                        </tr>
                        <tbody>
                        {this.getRows()}
                        </tbody>
                    </table>
                </div>
            );
        }
        return (
            <div>
                <h3>{this.props.badQueryMessage}</h3>
            </div>
        )
    }
}

export default ResultsTable;
