import {useEffect} from "react";

function ConnectionStatus() {
        useEffect(() => {
        setIsLoading(true)
        fetch(
            "http://127.0.0.1:8000/database-connections/get-status/"
        ).then((response) => {
            return response.json();
        }).then((data) => {
            setIsLoading(false);
            setLoadedUserData(data);
        }).catch(error => {
                setIsBadRequest(true);
                setIsLoading(false);
            }
        );
    }, [])
}

export default ConnectionStatus;
