import {Component, useRef, useState} from "react";
import ResultsTable from "./ResultsTable";
import {LineBreak} from "../LineBreak";


class QueryDBForm extends Component {
    constructor(props) {
        super(props);
        this.queryInputRef = useRef();
        this.state = {
            queryResults: []
        }
    }

    submit(event) {
        event.preventDefault();

        const submittedData = {
            query: queryInputRef.current.value,
        }
        fetch('http://127.0.0.1:8000/api/v1/database-connections/query-remote-db/',
            {
                method: "POST",
                body: JSON.stringify(submittedData),
                headers: {
                    'Content-Type': "application/json"
                }
            }
        ).then((response) => {
            return response.json();
        }).then((data) => {
            setHasResults(true)
            // queryResults = data;
        });
    }

    render() {
        return (
            <div>
                {/*<form onSubmit={this.submit}>*/}
                <form>
                    <div>
                        <label htmlFor="query-panel">Query Panel: </label>
                        <textarea rows="10" cols="50" id="query-panel" ref={queryInputRef} required/>
                    </div>
                    <br/>
                    <div>
                        <button className='btn'>Submit</button>
                    </div>
                </form>
                <LineBreak/>
                {
                    this.state.queryResults.length > 0 ?
                        <ResultsTable data={this.state.queryResults}/> : null
                }
            </div>
        );
    }
}

