import {Component} from "react";
import Row from "./Row";


class ResultsTable extends Component {
    constructor(props) {
        super(props);
    }


    getHeader = function () {
        if ("bad query" in this.props.data) {
            return <div/>
        }
        let columns = Object.keys(this.props.data[0]);
        return columns.map((column) => {
            return (
                <th>
                    {column.toUpperCase()}
                </th>
            );
        })
    }

    getRows = function () {
        if ("bad query" in this.props.data) {
            return <div/>
        }
        let rows = this.props.data;
        let indexes = Object.keys(this.props.data[0]);
        return rows.map((row) => {
            return (
                <tr>
                    <Row data={row} keys={indexes}/>
                </tr>
            );
        })
    }

    render() {
        return (
            <div>
                <table>
                    <tr>
                        {this.getHeader()}
                    </tr>
                    <tbody>
                    {this.getRows()}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default ResultsTable;
