import {Backdrop} from "./Backdrop";
import {ConfirmationModal} from "./ConfirmationModal";

function UserDetail(props) {
    function factory(id) {
        return props.onDelete(id)
    }
    return (
        <div>
            <li className='list'>
                {props.email + " - " + props.name}
                <button className='btn' value='delete-user' onClick={props.onClick}>Delete</button>
                <ConfirmationModal onCancel={props.onCancel}
                                   onDelete={factory()}
                                   isModalVisible={props.isModalVisible}
                                   id={props.id}
                />
                <Backdrop isModalVisible={props.isModalVisible}/>
                {/*{props.isModalVisible ? <Backdrop/> : null}*/}
                {/*{props.isModalVisible ? <ConfirmationModal onCancel={props.onCancel}*/}
                {/*                                           onDelete={props.onDelete}*/}
                {/*                                           id={props.id}/> : null}*/}
            </li>
            <br/>
        </div>
    )
}

export default UserDetail;
